QueryBlock.Query:
INSERT INTO {Товари_Залишки_TablePart.TABLE} ( uid, {Товари_Залишки_TablePart.Період}, {Товари_Залишки_TablePart.Номенклатура}, {Товари_Залишки_TablePart.Склад}, {Товари_Залишки_TablePart.Кількість}, {Товари_Залишки_TablePart.Сума} ) SELECT uuid_generate_v4(), date_trunc('day', Товари.period::timestamp) AS Період, Товари.{Товари_Const.Номенклатура} AS Номенклатура, Товари.{Товари_Const.Склад} AS Склад,
/* Кількість */ SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Кількість} ELSE -Товари.{Товари_Const.Кількість} END) AS Кількість,
/* Сума */ SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Сума} ELSE -Товари.{Товари_Const.Сума} END) AS Сума
FROM {Товари_Const.TABLE} AS Товари
WHERE date_trunc('day', Товари.period::timestamp) = @ПеріодДеньВідбір
GROUP BY Період, Номенклатура, Склад
HAVING
/* Кількість */ SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Кількість} ELSE -Товари.{Товари_Const.Кількість} END) != 0 OR
/* Сума */ SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Сума} ELSE -Товари.{Товари_Const.Сума} END) != 0

INSERT INTO {Товари_ЗалишкиТаОбороти_TablePart.TABLE} ( uid, {Товари_ЗалишкиТаОбороти_TablePart.Період}, {Товари_ЗалишкиТаОбороти_TablePart.Номенклатура}, {Товари_ЗалишкиТаОбороти_TablePart.Склад}, {Товари_ЗалишкиТаОбороти_TablePart.КількістьПрихід}, {Товари_ЗалишкиТаОбороти_TablePart.КількістьРозхід}, {Товари_ЗалишкиТаОбороти_TablePart.КількістьЗалишок}, {Товари_ЗалишкиТаОбороти_TablePart.СумаПрихід}, {Товари_ЗалишкиТаОбороти_TablePart.СумаРозхід}, {Товари_ЗалишкиТаОбороти_TablePart.СумаЗалишок} ) SELECT uuid_generate_v4(), date_trunc('day', Товари.period::timestamp) AS Період, Товари.{Товари_Const.Номенклатура} AS Номенклатура, Товари.{Товари_Const.Склад} AS Склад,
/* Кількість */ SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Кількість} ELSE 0 END) AS КількістьПрихід, SUM(CASE WHEN Товари.income = false THEN Товари.{Товари_Const.Кількість} ELSE 0 END) AS КількістьРозхід, SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Кількість} ELSE -Товари.{Товари_Const.Кількість} END) AS КількістьЗалишок,
/* Сума */ SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Сума} ELSE 0 END) AS СумаПрихід, SUM(CASE WHEN Товари.income = false THEN Товари.{Товари_Const.Сума} ELSE 0 END) AS СумаРозхід, SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Сума} ELSE -Товари.{Товари_Const.Сума} END) AS СумаЗалишок
FROM {Товари_Const.TABLE} AS Товари
WHERE date_trunc('day', Товари.period::timestamp) = @ПеріодДеньВідбір
GROUP BY Період, Номенклатура, Склад
HAVING
/* Кількість */ SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Кількість} ELSE 0 END) != 0 OR SUM(CASE WHEN Товари.income = false THEN Товари.{Товари_Const.Кількість} ELSE 0 END) != 0 OR SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Кількість} ELSE -Товари.{Товари_Const.Кількість} END) != 0 OR
/* Сума */ SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Сума} ELSE 0 END) != 0 OR SUM(CASE WHEN Товари.income = false THEN Товари.{Товари_Const.Сума} ELSE 0 END) != 0 OR SUM(CASE WHEN Товари.income = true THEN Товари.{Товари_Const.Сума} ELSE -Товари.{Товари_Const.Сума} END) != 0

INSERT INTO {Товари_Підсумки_TablePart.TABLE} ( uid, {Товари_Підсумки_TablePart.Номенклатура}, {Товари_Підсумки_TablePart.Склад}, {Товари_Підсумки_TablePart.Кількість}, {Товари_Підсумки_TablePart.Сума} ) SELECT uuid_generate_v4(), Товари.{Товари_Залишки_TablePart.Номенклатура} AS Номенклатура, Товари.{Товари_Залишки_TablePart.Склад} AS Склад,
/* Кількість */ SUM(Товари.{Товари_Залишки_TablePart.Кількість}) AS Кількість,
/* Сума */ SUM(Товари.{Товари_Залишки_TablePart.Сума}) AS Сума
FROM {Товари_Залишки_TablePart.TABLE} AS Товари
GROUP BY Номенклатура, Склад
HAVING
/* Кількість */ SUM(Товари.{Товари_Залишки_TablePart.Кількість}) != 0 OR
/* Сума */ SUM(Товари.{Товари_Залишки_TablePart.Сума}) != 0
